import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function Edit(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    data["id"] = props.id;
    updateIndicator(data);
  };

  const updateIndicator = (data) => {
    axios.put("http://localhost:3001/indicators", data).then((res) => {
      props.setIndicators(
        props.indicators.map((item) => {
          return item.id === props.id
            ? {
                id: item.id,
                indicator_title: item.indicator_title,
                indicator_description: item.indicator_description,
                indicator_rating: item.indicator_rating,
              }
            : item;
        })
      );
    });
  };

  return (
    <form className="add-indicator" onSubmit={handleSubmit(onSubmit)}>
      <textarea
        type="text"
        placeholder="Description..."
        name="indicator_description"
        {...register('indicator_description', { required: true })}
      />

      <select
        class="formselect"
        placeholder="Rating"
        name="indicator_rating"
        {...register("indicator_rating", { required: true })}
      >

        <option value="1">Strategic</option>
        <option value="2">Tactical</option>
        <option value="3">Operational</option>
      </select>

      <input className='btn btn-outline-success' type="submit" />
    </form>
  );
}
