import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import Delete from "../indicators_lib/delete_indicator";

export default function Edit(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    data["id"] = props.id;
    updateVisualization(data);
  };

  const updateVisualization = (data) => {
    axios.put("http://localhost:3001/visualizations", data).then((res) => {
      props.setVisualizations(
        props.visualizations.map((item) => {
          return item.id === props.id
            ? {
                id: item.id,
                visualization_title: item.visualization_title,
                visualization_description: item.visualization_description,
                visualization_class: item.visualization_class,
              }
            : item;
        })
      );
    });
  };

  return (
    <form className="add-visualization" onSubmit={handleSubmit(onSubmit)}>
      <textarea
        type="text"
        placeholder="Description..."
        name="visualization_description"
        {...register('visualization_description', { required: true })}
      />

      <select
        class="formselect"
        placeholder="Rating"
        name="visualization_class"
        {...register("visualization_class", { required: true })}
      >

        <option value="Distribution">Distribution</option>
                <option value="Correlation">Correlation</option>
                <option value="Ranking">Ranking</option>
                <option value="Part of a Whole">Part of a Whole</option>
                <option value="Evolution">Evolution</option>
                <option value="Map">Map</option>
                <option value="Flow">Flow</option>

      </select>

      <input className='btn btn-outline-success' value="Update" type="submit" />
      <Delete
                id={props.id}
                visualizations={visualizations}
                setVisualizations={setVisualizations}
              />
    </form>
  );
}
