import React, { useState } from "react";
import './chart.css'

const Chart = (props) => {
  const [chartTypes, setChartTypes] = useState([]);

  const [charts, setChart] = useState([]);

  const handleClassChange = (e) => {
    console.log(e.target.value);
    switch (e.target.value) {
      case "bar_class":
        setChartTypes([
          { key: "bar_h", title: "Vertical bar chart" },
          { key: "bar_v", title: "Horizontal bar chart" },
          { key: "bar_m", title: "Multi axis bar chart" },
        ]);
        break;
      default:
        setChartTypes([
          { key: "bar_h", title: "Vertical bar chart" },
          { key: "bar_v", title: "Horizontal bar chart" },
          { key: "bar_m", title: "Multi axis bar chart" },
        ]);
    }
    setChart(
      chartTypes.map((obj) => {
        return <option key={obj.key}>{obj.title}</option>;
      })
    );
  };

  const [visuElement, setVisuElement] = useState(
    <div classname="chart_box">
      <img
        classname="card-img-top"
        src={process.env.PUBLIC_URL + "/charts/test.png"}
        alt="Card image cap"
      />
      <div classname="card-body">
        <p classname="card-text">
          This type of charts can be used for <b>displaying...</b> and{" "}
          <b>....</b>
        </p>
      </div>
    </div>
  );

  const handleTypeChange = (e) => {
    let filtered = chartTypes.reduce(
      (a, o) => (o.title == e.target.value && a.push(o.key), a),
      []
    );

    console.log(filtered[0]);

    setVisuElement(
      <div classname="chart_box">
        <img id='chart_stakeholder'
          classname="card-img-top"
          src={process.env.PUBLIC_URL + "/charts/"+filtered[0]+".png"}
          alt="Card image cap"
        />
        <div classname="card-body">
          <p classname="card-text">
            This type of charts can be used for <b>displaying...</b> and{" "}
            <b>....</b>
          </p>
        </div>
      </div>
    );
  };

  return (
    <div className="row">
      <div className="row">
        <label for="chart" className="form-label">
          Class of charts
        </label>
        <select
          className="form-select"
          id="chart"
          placeholder="chartclass"
          onChange={handleClassChange}
          required
        >
          <option value="bar_class">Bar charts</option>
          <option value="line_class">Line charts</option>
          <option value="area_class">Area charts</option>
          <option value="other_class">Other charts</option>
        </select>
      </div>
      <div className="row"></div>
      <label for="chart" className="form-label">
        Chart name
      </label>
      <select
        className="form-select"
        id="chartname"
        placeholder="chartname"
        onChange={handleTypeChange}
        required
      >
        {charts}
      </select>
      <hr className="my-3" />
      {visuElement}
    </div>
  );
};
export default Chart;
