import "../library-view.css";
import React, { useContext, useState } from "react";
import Modal from "react-bootstrap/Modal";
import { Button } from "react-bootstrap";
import { CPanelContext } from "../cpanels";

export const chartContext = React.createContext();

const CPanelItem = (props) => {
  const [
    currentCPanel,
    setCurrentCPanel,
    currentPanel,
    setCurrentPanel,
    pList,
    setpList,
    panels,
    visualizations,
    indicators,
  ] = useContext(CPanelContext);

  const [currentVisualization, setCurrentVisualization] = useState();
  const [currentIndicator, setCurrentIndicator] = useState();

  const getPanElement = (i) => {
    panels.map((item) => {
      if (item.id == i) setCurrentPanel(item);
    });
    //console.log(currentPanel)
  };

  const getVizElement = () => {
    if (!currentPanel) return{
      title:' ',
      description: ' ',
      chart: ''
    };
    visualizations.map((item) => {
      if (item.id == currentPanel.visualization_id) return(item);
    });
    return{
      title:' ',
      description: ' ',
      chart: ''
    };
  };

  const getIndElement = () => {
    
    let res = {
      title:' ',
      description: ' '
    };
    if (!currentPanel) return res;
    indicators.map((item) => {
      if (item.id == currentPanel.indicator_id) res=item;
    });
    console.log(currentPanel.indicator_id);
    return res;
  };

  const decodeChart = (elt) => {
    if (!elt) return;
    let blob = elt.chart;
    if (typeof blob === "string") return blob;
    const { data } = blob;
    const img = new Buffer.from(data).toString("ascii");

    return img;
  };

  return (
    <>
      {currentCPanel === undefined ? (
        " "
      ) : (
        <Modal
          show={props.show}
          backdrop="static"
          keyboard={true}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          dialogClassName="modal-style"
          scrollable
          centered
        >
          <Modal.Header>
            <Modal.Title>{currentCPanel.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <table className="table table-dark">
              <tbody>
                <tr>
                  <td>
                    <b>Select a panel to insert</b>
                  </td>
                  <td>
                    <select
                      className="formselect"
                      name="panel"
                      onChange={(e) => getPanElement(e.target.value)}
                    >
                      {panels.map((item) => {
                        return <option value={item.id}>{item.title}</option>;
                      })}
                    </select>
                  </td>
                </tr>
                <tr>
                  <td colSpan="2">
                    Description : <i>{currentPanel.description}</i>
                  </td>
                </tr>
                <tr>
                  <td>
                    Associated indicator : <b>{getIndElement().title}</b> 
                  </td>
                  <td>
                    <i>Associated visualization</i>
                  </td>
                </tr>
              </tbody>
            </table>
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              size="sm"
              variant="secondary"
              onClick={props.onClose}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              size="sm"
              variant="secondary"
              onClick={props.onSave}
            >
              Save
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};
export default CPanelItem;
