import React, { useState } from "react";
import ReactJson from "react-json-view";
import jsonfile from "jsonfile";
import dash from "../../../data/template_dash.json";
import FileSaver from "file-saver";

import "./generate-component.css";
const GenerateComponent = () => {
  const file = "../../../data/template_dash.json";
  const readJSonFile = () => {
    const filename = "f";
    const fileData = JSON.stringify(dash);
    const blob = new Blob([fileData], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = `${filename}.json`;
    link.href = url;
    //link.click();

    var file = new File(["Hello, world!"], " ");
    FileSaver.saveAs(file);
  };
  return (
    <div className="row">
      <div className="card  row">
        <ul class="nav nav-pills mb-3 row" id="pills-tab" role="tablist">
          <li class="nav-item">
            <a
              class="nav-link active"
              id="pills-home-tab"
              data-toggle="pill"
              href="#pills-home"
              role="tab"
              aria-controls="pills-home"
              aria-selected="true"
            >
              Home
            </a>
          </li>
          <li class="nav-item">
            <a
              class="nav-link"
              id="pills-profile-tab"
              data-toggle="pill"
              href="#pills-profile"
              role="tab"
              aria-controls="pills-profile"
              aria-selected="false"
            >
              Profile
            </a>
          </li>
          <li class="nav-item">
            <a
              class="nav-link"
              id="pills-contact-tab"
              data-toggle="pill"
              href="#pills-contact"
              role="tab"
              aria-controls="pills-contact"
              aria-selected="false"
            >
              Contact
            </a>
          </li>
        </ul>
        <div className="card-header bg-secondary row">
          Dashboard JSon structure
          <span className="btn btn-success btn-sm " onClick={readJSonFile}>
            Save the dashboard
          </span>
        </div>
        <div className="card-body">
          <ReactJson src={dash} collapsible view="dual" />
        </div>
      </div>
    </div>
  );
};

export default GenerateComponent;
