import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function AddVisualization(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addVisualization(data);
  };

  const addVisualization = (data) => {
    axios.post("http://localhost:3001/visualizations", data).then(() => {
      props.setVisualizations([
        ...props.visualizations,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-visualization" onSubmit={handleSubmit(onSubmit)}>
      <h4>Introduce a new type of visualizations</h4>
      <table>
        <tbody>
          <tr>
            <td>Name</td>
            <td>
              <input
                type="text"
                placeholder="Visualization Title"
                name="visualization_title"
                {...register("visualization_title", { required: true })}
              />
            </td>
          </tr>
          <tr>
            <td>Class</td>
            <td>
              <select
                class="formselect"
                placeholder="Rating"
                name="visualization_rating"
                {...register("visualization_rating", { required: true })}
              >
                <option value="1">Strategic</option>
                <option value="2">Tactical</option>
                <option value="3">Operational</option>
              </select>
            </td>
          </tr>
          <tr>
            <td>Description</td>
            <td>
              <textarea
                type="text"
                placeholder="Description..."
                name="visualization_description"
                {...register("visualization_description", { required: true })}
              />
            </td>
          </tr>
          <tr>
          <td></td>
            <td><input className="btn btn-primary" value="Submit" type="submit" /></td>
          </tr>
        </tbody>
      </table>

    </form>
  );
}
