import React, { useState, useContext } from "react";
import IndicatorChooser from "./indicator-chooser";
import Chart from "./chart";
import Select from "react-select";
import "./frame-reference.css";
import Frame from "../util/frame";
import { AppContext } from '../specification-wizard';

const FrameReference = (props) => {
  const initContext = useContext(AppContext);

  const updateContext=()=>{ alert('ok')
  const ladContext = initContext.dashboardStructure;
  const setLadContext = initContext.setDashboardStructure;
  
  
  setLadContext((prevState) => {
    return { ...prevState, ["title"]: 'Updated Title' };
  })
  console.log(ladContext);
};
  return (
    <div class="row">
      <div class="form-group row">
      <div class="row">
      
        <h3>
          Frame of reference for situation perception
        </h3>
        <button onClick={updateContext} />
        <panel>
          Decsribe what main indicators that allow you to perform your analysis
          and take the appropriate decision
        </panel>
        <hr className="my-1" />
      </div>
      <div className="col-md-6 mt-2">
      <IndicatorChooser />
      </div>
      <div className="col-md-6 mt-2 px-4">
        <Chart />
      </div>
    </div>
    <Frame />
    </div>
  );
};
export default FrameReference;
