import React, { useState } from "react";
import ReactJson from "react-json-view";
import jsonfile from "jsonfile";
import dash from "../../../data/template_dash.json";
import FileSaver from "file-saver";

import "./generate-component.css";
const GenerateComponent = () => {
  const file = "../../../data/template_dash.json";
  const readJSonFile = () => {
    const filename = "f";
    const fileData = JSON.stringify(dash);
    const blob = new Blob([fileData], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = `${filename}.json`;
    link.href = url;
    //link.click();

    var file = new File(["Hello, world!"], " ");
    FileSaver.saveAs(file);
  };
  return (
    <div className="row">
      <div className="modal-dialog row" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title">Modal title</h5>
        <button type="button" className="save" data-dismiss="modal" aria-label="Close" onClick={readJSonFile}>
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
      <ReactJson src={dash} collapsible view="dual" />
      </div>
      
    </div>
  </div>
      </div>
  );
};

export default GenerateComponent;
