import "./library-view.css";

import { useState, useEffect } from "react";
import AddIndicator from "./indicators_lib/add_indicator";
import Edit from "./indicators_lib/edit_indicator";
import Delete from "./indicators_lib/delete_indicator";
import axios from "axios";

function Indicators() {
  const [indicators, setIndicators] = useState([]);

  const getIndicators = () => {
    axios.get("http://localhost:3001/indicators").then((res) => {
      setIndicators(res.data);
    });
  };

  useEffect(() => {
    getIndicators();
  }, [indicators]);

  return (
    <>
      <AddIndicator indicators={indicators} setIndicators={setIndicators} />
      <div className="indicators">
        {indicators.map((item) => {
          return (
            <div className="indicator">
              <h3>Title: {item.indicator_title}</h3>
              <h3>Review: {item.indicator_description}</h3>
              <h3>Rating: {item.indicator_rating}</h3>
              <Edit id={item.id} indicators={indicators} setIndicators={setIndicators} />
              <Delete id={item.id} indicators={indicators} setIndicators={setIndicators} />
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Indicators;
