import React, { useState } from "react";
import IndicatorChooser from "./indicator-chooser";

const KPI = (props) => {
  let objectives = ["1", "2", "3"];

  const [objectiveOptions, setobjectiveOptions] = useState(
    objectives.map((obj) => {
      return <option key={obj}>{obj}</option>;
    })
  );

  let indicators = ["1", "2", "3"];
  let indicatorOptions = indicators.map((obj) => {
    return <option key={obj}>{obj}</option>;
  });

  const handletypeClasseChange = (e) => {
    console.log(e.target.value);
    switch (e.target.value) {
      case "class_1":
        objectives = [
          { key: "obj1_1", title: "Relationship to Time" },
          {
            key: "obj1_2",
            title:
              "Maximize the efficiency of document search processing (consultation)",
          },
        ];
      case "class_2":
        objectives = [
          {
            key: "obj2_1",
            title: "Maximize the relevance of the information search",
          },
        ];
      case "class_3":
        objectives = [
          { key: "obj3_1", title: "To keep track of the student's activity" },
          { key: "obj3_2", title: "Observance of instructions" },
          { key: "obj3_3", title: "Safeguarding of productions" },
        ];
      case "class_4":
        objectives = [
          { key: "obj4_1", title: "Understanding of student behavior" },
          { key: "obj4_1", title: "To improve thinking" },
        ];
    }
    setobjectiveOptions(
      objectives.map((obj) => {
        return <option key={obj.key}>{obj.title}</option>;
      })
    );
  };
  return (
    <form className="needs-validation" novalidate>
      <div class="form-group row">
        <h3>Key indicators</h3>
        <panel>
          Decsribe what main indicators that allow you to perform your analysis
          and take the appropriate decision
        </panel>
        <hr className="my-1" />
      </div>
      <div className="col-md-8 mt-2 ">
        <label for="country" className="form-label">
          Class of the key indicator
        </label>
        <select
          className="form-select"
          id="country"
          placeholder="Your role"
          onChange={handletypeClasseChange}
          required
        >
          <option value="class_1">Monitoring the use of media and tools</option>
          <option value="class_2">Monitoring Information Retrieval</option>
          <option value="class_3">Monitoring of student activity</option>
          <option value="class_4">Monitoring comprehension</option>
        </select>
        <hr className="my-3" />

        <label for="country" className="form-label">
          Objective of the key indicator
        </label>
        <select
          className="form-select"
          id="country"
          placeholder="Your role"
          value="typeDecision"
          required
        >
          {objectiveOptions}
        </select>
        <hr className="my-3" />

        <label for="country" className="form-label">
          Name of the key indicator
        </label>
        <select
          className="form-select"
          id="country"
          placeholder="Your role"
          value="typeDecision"
          onChange="handletypeDecision"
          required
        >
          {indicatorOptions}
        </select>
        <hr className="my-3" />
      </div>
      <div></div>
      <IndicatorChooser IndicatorClass="classe" IndicatorClass="indicator" />
    </form>
  );
};
export default KPI;
