import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import Indicators from "../indicators";

export default function AddFrame(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addFrame(data);
  };

  const addFrame = (data) => {
    axios.post("http://localhost:3001/frames", data).then(() => {
      props.setFrames([
        ...props.frames,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-frame" onSubmit={handleSubmit(onSubmit)}>
     <h4>Define a new type of frames</h4>
      <table className="table table-dark">
        <tbody>
          <tr>
            <td>Name</td>
            <td>
              <input
                type="text"
                placeholder="Frame Title"
                name="frame_title"
                {...register("frame_title", { required: true })}
              />
            </td>
            <td>Class</td>
            <td>
              <select
                class="formselect"
                placeholder="Rating"
                name="frame_rating"
                {...register("frame_rating", { required: true })}
              >
                <option value="1">Elaboration</option>
                <option value="2">Inquiry</option>
                <option value="3">Preservation</option>
                <option value="4">Comparison</option>
                <option value="5">Reframing</option>
                <option value="6">Seeking</option>
              </select>
            </td>
          </tr>
          <tr>
          <td>Associated indicator</td>
          <td>
              <select
                class="formselect"
                placeholder="Indicators"
                name="frame_rating"
                {...register("frame_rating", { required: true })}
              >

                <option value="1">Elaboration</option>
                <option value="2">Inquiry</option>
                <option value="3">Preservation</option>
                <option value="4">Comparison</option>
                <option value="5">Reframing</option>
                <option value="6">Seeking</option>
              </select>
            </td>
            <td>Graphical representation</td>
          <td>
              <select
                class="formselect"
                placeholder="Indicator"
                name="frame_indicator"
                {...register("frame_indicator", { required: false })}
              >
              {Indicators.map((item)=>{
                return(<option value="1">test</option>);
              })}
              </select>
            </td>
          </tr>
          <tr>
            <td>Description</td>
            <td>
              <textarea
                type="text"
                placeholder="Description..."
                name="frame_description"
                {...register("frame_description", { required: true })}
              />
            </td>
          </tr>

          <tr>
            <td></td>
            <td>
              <input className="btn btn-primary" value="Submit" type="submit" />
            </td>
          </tr>
        </tbody>
      </table>
    </form>
  );
}
