import "../library-view.css";
import React, { useState } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import ChartUpload from "../chart-upload";
import Modal from "react-bootstrap/Modal";
import { Button } from "react-bootstrap";

export default function Actions(props) {
  const { register, handleSubmit } = useForm();
  
  const onSubmit = (data) => {
    data["id"] = props.id;
    data.chart = chart;
    
    updateVisualization(data);
  };

  const updateVisualization = (data) => {
    axios.put("http://localhost:3001/visualizations", data).then((res) => {
      props.setVisualizations(
        props.visualizations.map((item) => {
          return item.id === props.id
            ? {
                id: item.id,
                title: item.title,
                description: item.description,
                class: item.class,
                chart: item.chart,
              }
            : item;
        })
      );
    });
    props.updateDisplay();
  };

  const [chart, setChart] = React.useState(null);

  const handleChartChange = (v) => {
    setChart(v);
  };

  return (
    <Modal
   show={true}
    backdrop="static"
    keyboard={true}
    size="lg"
    aria-labelledby="contained-modal-title-vcenter"
    dialogClassName="modal-style"
    scrollable
    centered
  >
    <Modal.Header>
      <Modal.Title>Configure the frame</Modal.Title>
    </Modal.Header>
    <Modal.Body>
    
    </Modal.Body>
              <Modal.Footer>
                <Button  size="sm" variant="secondary">
                  Close
                </Button>
                <Button
                  type="submit"
                  size="sm"
                  variant="secondary"
               
                >
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
  );
}
