import "./library-view.css";

import React, { useState, useEffect } from "react";
import AddDecision from "./decisions_lib/add_decision";
import Edit from "./decisions_lib/edit_decision";
import Delete from "./decisions_lib/delete_decision";
import axios from "axios";
import CssBaseline from "@material-ui/core/CssBaseline";

function Decisions() {
  const [decisions, setDecisions] = useState([]);

  const getDecisions = () => {
    axios.get("http://localhost:3001/decisions").then((res) => {
      setDecisions(res.data);
    });
  };

  useEffect(() => {
    getDecisions();
  }, [decisions]);

  const columns = React.useMemo(
    () => [
      {
        Header: 'Name',
        columns: [
          {
            Header: 'First Name',
            accessor: 'firstName',
          },
          {
            Header: 'Last Name',
            accessor: 'lastName',
          },
        ],
      },
      {
        Header: 'Info',
        columns: [
          {
            Header: 'Age',
            accessor: 'age',
          },
          {
            Header: 'Visits',
            accessor: 'visits',
          },
          {
            Header: 'Status',
            accessor: 'status',
          },
          {
            Header: 'Profile Progress',
            accessor: 'progress',
          },
        ],
      },
    ],
    []
  );
  const data = React.useMemo(() => makeData(20), [])

  return (
    <>
    <div>
      <CssBaseline />
      <Table columns={columns} data={data} />
    </div>

      <AddDecision decisions={decisions} setDecisions={setDecisions} />
      <div className="decisions">
        {decisions.map((item) => {
          return (
            <div className="decision">
              <h3>Title: {item.decision_title}</h3>
              <h3>Review: {item.decision_description}</h3>
              <h3>Rating: {item.decision_rating}</h3>
              <Edit id={item.id} decisions={decisions} setDecisions={setDecisions} />
              <Delete id={item.id} decisions={decisions} setDecisions={setDecisions} />
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Decisions;
