import React, { useState } from "react";
import IndicatorChooser from "./frame-indicator"
import Chart from "../steps/chart";
import Select from "react-select";
import "./frame.css";

const Frame = (props) => {
  
  return (
    <div class="row">
      <div class="form-group row">
      <div class="row">
        <h5>
          Constructing a frame
        </h5>
        <panel>
          Decsribe what indicators allow you to perform your analysis
          and take the appropriate decision
        </panel>
        <hr className="my-1" />
      </div>
      <div className="col-md-6 mt-2">
      <IndicatorChooser />
      </div>
      <div className="col-md-6 mt-2 px-4">
        <Chart />
      </div>
    </div>
    </div>
  );
};
export default Frame;
