import React, { useState } from "react";
import "./frame.css";
import FrameIndicator from "./frame-indicator";
import Modal from "react-bootstrap/Modal";
import { Button } from "react-bootstrap";

const Frame = (props) => {
  const [frames, setFrames] = useState([]);
  const [show, setShow] = useState(false);
  const [new_frame_data, setNewFrameData] = useState({
    title: "UNTITLED",
    type: "UNTYPED",
  });

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleSave = (e) => {
    
    setFrames(
      frames.concat(
        <FrameIndicator position={frames.length + 1} framename={new_frame_data.title} />
      )
    );
  };
  const updateDialogData = (type, ev) => {
    setNewFrameData(prevState => {
      return {...prevState, [type]:'title'  };
    });    
  };

  const frameAdd = () => {
    const enteredName = prompt(
      "Please enter a name for this frame",
      "NewFrame" + (frames.length + 1)
    );
    setFrames(
      frames.concat(
        <FrameIndicator position={frames.length + 1} framename={enteredName} />
      )
    );
  };

  const deleteFrame = (e) => {
    let a = frames.filter((item) => item.position !== e);
    console.log(a);
  };

  return (
    <div class="row " class="div_new_frame">
      <div className="float-right">{frames}</div>
      <hr />
      <div className="f_buttons">
        <span className="btn btn-secondary" onClick={() => frameAdd()}>
          Creat a new associated frame
        </span>
        <div>
          <span className="btn btn-secondary" onClick={handleShow}>
            Launch demo modal
          </span>

          <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={false}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            scrollable
            centered
          >
            <Modal.Header>
              <Modal.Title>Configure the frame</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form>
                <div className="form-group">
                  <label for="frameTitle">Frame name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="frameTitle"
                    placeholder="Enter a title for the new frame"
                    value={new_frame_data.title}
                    onChange={(e) => updateDialogData("title", e)}
                  />
                  <small id="emailHelp" className="form-text text-muted">
                    Please provide a concise name.
                  </small>
                </div>

                <div className="form-group">
                  <label for="select_relation">
                    Relation to the original frame
                  </label>
                  <select
                    className="form-control"
                    id="select_relation"
                    placeholder="Enter a title for the new frame"
                  >
                    <option></option>
                    <option></option>
                    <option></option>
                  </select>
                  <small id="emailHelp" className="form-text text-muted">
                    Please provide a concise name.
                  </small>
                </div>
              </form>
            </Modal.Body>
            <Modal.Footer>
              <span className="btn btn-sm btn-primary" onClick={handleClose}>
                Close
              </span>
              <Button
                type="submit"
                className="btn btn-sm btn-outline-primary"
                onClick={handleSave}
              >
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      </div>
    </div>
  );
};
export default Frame;
