import React, { useState } from "react";
import "./frame.css";
import FrameIndicator from "./frame-indicator";
import Modal from "react-bootstrap/Modal";
import { Button } from "react-bootstrap";

const Frame = (props) => {
  const [frames, setFrames] = useState([]);
  const [show, setShow] = useState(false);
  const [new_frame_data, setNewFrameData] = useState({
    title: "UNTITLED",
    type: "UNTYPED",
    description: "UNDESCRIBED"
  });

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleSave = (e) => {
    
    setFrames(
      frames.concat(
        <FrameIndicator position={frames.length + 1} framename={new_frame_data.title} />
      )
    );
    setShow(false);
  };
  const updateDialogData = (type, ev) => {
    setNewFrameData(prevState => {
      return {...prevState, [type]:ev.target.value  };
    });    
  };
 const updateDialogDataDescription = (ev) =>{
   let description = '' ;
   switch(ev.target.value){
    case "class_1":'rrr'
   }
   
  setNewFrameData(prevState => {
    return {...prevState, ['description']:description};
  }); 
 }
  

  const deleteFrame = (e) => {
    let a = frames.filter((item) => item.position !== e);
    console.log(a);
  };

  return (
    <div class="row " class="div_new_frame">
      <div className="float-right">{frames}</div>
      <hr />
      <div className="f_buttons">
        <span className="btn btn-secondary" onClick={handleShow}>
          Creat a new associated frame
        </span>
        <div>

          <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={false}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            scrollable
            centered
          >
            <Modal.Header>
              <Modal.Title>Configure the frame</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form>
                <div className="form-group">
                  <label for="frameTitle">Frame name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="frameTitle"
                    placeholder="Enter a title for the new frame"
                    value={new_frame_data.title}
                    onChange={(e) => updateDialogData("title", e)}
                  />
                  <small id="emailHelp" className="form-text text-muted">
                    Please provide a concise name.
                  </small>
                </div>

                <div className="form-group">
                  <label for="select_relation">
                    Relation to the original frame
                  </label>
                  <select
                    className="form-control"
                    id="select_relation"
                    placeholder="Enter a title for the new frame"
                    value={new_frame_data.type}
                    onChange={(e) => {updateDialogData("type", e); updateDialogDataDescription(e)}}
                  >
                    <option>Elaboration</option> 
                    <option>Inquiry</option>
                    <option>Preservation</option>
                    <option>Comparison</option>
                    <option>Reframing</option>
                    <option>Seeking</option>
                    <option>(Other)</option>
                  </select>
                  <small id="emailHelp" className="form-text text-muted">
                  {new_frame_data.description}
                  </small>
                </div>
              </form>
            </Modal.Body>
            <Modal.Footer>
              <span className="btn btn-sm btn-primary" onClick={handleClose}>
                Close
              </span>
              <Button
                type="submit"
                className="btn btn-sm btn-outline-primary"
                onClick={handleSave}
              >
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      </div>
    </div>
  );
};
export default Frame;
