import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function AddIndicator(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addIndicator(data);
  };

  const addIndicator = (data) => {
    axios.post("http://localhost:3001/indicators", data).then(() => {
      props.setIndicators([
        ...props.indicators,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-indicator" onSubmit={handleSubmit(onSubmit)}>
      <h4>Introduce a new type of indicators</h4>
      <table>
       <tbody>
          <tr>
            <td>Name</td>
            <td></td>
          </tr>
          <tr>
            <td>Class</td>
            <td></td>
          </tr>
          <tr>
            <td>Description</td>
            <td></td>
          </tr>
        </tbody>
      </table>
      <div>
        Title
        <input
          type="text"
          placeholder="Indicator Title"
          name="indicator_title"
          {...register("indicator_title", { required: true })}
        />
      </div>
      Description
      <textarea
        type="text"
        placeholder="Description..."
        name="indicator_description"
        {...register("indicator_description", { required: true })}
      />
      Class
      <select
        class="formselect"
        placeholder="Rating"
        name="indicator_rating"
        {...register("indicator_rating", { required: true })}
      >
        <option value="1">Strategic</option>
        <option value="2">Tactical</option>
        <option value="3">Operational</option>
      </select>
      <input className="btn btn-primary" value="Submit" type="submit" />
    </form>
  );
}
