import React, { useState, useContext } from "react";
import { VizContext } from "./visualizations";

const ChartUpload = (props) => {
  const [element, setElement, chart, setChart] = useContext(VizContext);

  const [picture, setPicture] = useState(null);
  const [imgData, setImgData] = useState(null);

  const encodeChart = (blob) => {
    console.log(blob);
    if (!blob) return;
    const { data } = blob;
    const img = new Buffer.from(data).toString("ascii");
    console.log(img);
    return img;
  };

  const onChangePicture = (e) => {
    if (e.target.files[0]) {
      setPicture(e.target.files[0]);
      const reader = new FileReader();
      reader.addEventListener("load", () => {
        
        props.onChartChange(reader.result);
        
      });
      reader.readAsDataURL(e.target.files[0]);
    }
  };
  return (
    <div className="formInstructionsDiv formElement">
      <p className="instructionsText" />
      <div className="register_profile_image">
        <input
          id="chart"
          type="file"
          onChange={onChangePicture}
          accept="image/*"
        />
      </div>

      <div className="chart_box">
         <img className="chart_box-image" src={encodeChart(element.chart)} /> 
      </div>
    </div>
  );
};

export default ChartUpload;
