import "./library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function Add(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addReview(data);
  };

  const addReview = (data) => {
    axios.post("http://localhost:3000/reviews", data).then(() => {
      props.setReviews([
        ...props.reviews,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-review" onSubmit={handleSubmit(onSubmit)}>
      <h4>Add Review</h4>
      <input
        type="text"
        placeholder="Book Title"
        name="book_title"
        {...register('book_title', { required: true })}

      />
      <input
        type="text"
        placeholder="Review"
        name="book_review"
        {...register('book_review', { required: true })}

      />
      <input
        type="number"
        placeholder="Rating"
        name="book_rating"
        {...register('book_rating', { required: true })}

      />

      <input id="btn" type="submit" />
    </form>
  );
}
