import "./library-view.css";

import React, { useState, useEffect } from "react";
import Actions from "./panels_lib/panel_actions";
import axios from "axios";
import {
  AddBoxRounded,
  EditRounded,
  HighlightOffRounded,
} from "@material-ui/icons";

export const PanelContext = React.createContext();

function Panels() {
  const [panels, setPanels] = useState([]);

  const [showModal, setShowModal] = useState(false);

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleModalSave = () => {
    if (creationMode)
      axios
        .post("http://localhost:3001/panels", currentElement)
        .then(() => {
          setPanels([
            ...panels,
            {
              data: currentElement,
              headers: { "Content-Type": "multipart/form-data" },
            },
          ]);
          updateDisplay();
        });
    else
      axios
        .put("http://localhost:3001/panels", currentElement)
        .then((res) => {
          setPanels(
            panels.map((item) => {
              return item.id === currentElement.id
                ? {
                    id: item.id,
                    title: item.title,
                    description: item.description,
                    class: item.class,
                  }
                : item;
            })
          );
          updateDisplay();
        });

    setShowModal(false);
    updateDisplay();
  };

  const deletePanel = (id) => {
    window.confirm(
      "Are you sure you want to delete this type of panels?"
    )
      ? axios
          .delete(`http://localhost:3001/panels/${id}`)
          .then((res) => {
            setPanels(
              panels.filter((item) => {
                return item.id !== id;
              })
            );
          })
      : (id = id);
    updateDisplay();
  };

  const getPanels = () => {
    axios.get("http://localhost:3001/panels").then((res) => {
      setPanels(res.data);
    });
  };

  useEffect(() => {
    getPanels();
  }, []);

  const updateDisplay = () => {
    getPanels();
  };


  const [currentElement, setCurrentElement] = useState();
  const [creationMode, setCreationMode] = useState(true);
  const panelContext = [currentElement, setCurrentElement];

  const CreateNewPanel = () => {
    const item = {
      title: "New",
      description: "Description",
      class: " ",
    };

    setCurrentElement(item);
    setCreationMode(true);
    setShowModal(true);

    return;
  };
  const EditPanel = (item) => {
    setCurrentElement(item);
    setCreationMode(false);
    setShowModal(true);
  };
  return (
    <PanelContext.Provider value={panelContext}>
      <Actions
        title="un titre"
        show={showModal}
        mode="Creation"
        panels={panels}
        setPanels={setPanels}
        updateDisplay={updateDisplay}
        onClose={handleModalClose}
        onSave={handleModalSave}
      />

      <div>
        <button className="btn btn-success" onClick={CreateNewPanel}>
          <AddBoxRounded /> &nbsp; New panel type
        </button>
      </div>
      <table className="table table-bordered table-hover table-dark table-striped text-md-start">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Class</th>
            <th scope="col">Description</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {panels.map((item) => {
            return (
              <tr key={item.id}>
                <th scope="row">{item.id}</th>
                <td>{item.title}</td>
                <td>{item.class}</td>               
                <td>{item.description}</td>

                <td>
                  <button
                    className="btn btn-sm btn-outline-primary"
                    onClick={() => EditPanel(item)}
                  >
                    <EditRounded />
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => deletePanel(item.id)}
                  >
                    <HighlightOffRounded />
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </PanelContext.Provider>
  );
}

export default Panels;
