import React, { useState } from "react";
import IndicatorChooser from "./indicator-chooser";
import Chart from "./chart";

const KPI = (props) => {
  const [objectives, setObjectives] = useState([]);
  const [indicators, setIndicators] = useState([]);
  const [indicatorClass, setIndicatorClass] = useState();
  const [objectiveValue, setObjectiveValue] = useState();
  const [indicatorValue, setIndicatorValue] = useState();

  const handleClasseChange = (e) => {
    switch (e.target.value) {
      case "class_1":
        setObjectives([
          { key: "obj1_1", title: "Relationship to Time" },
          {
            key: "obj1_2",
            title:
              "Maximize the efficiency of document search processing (consultation)",
          },
        ]);
        break;
      case "class_2":
        setObjectives([
          {
            key: "obj2_1",
            title: "Maximize the relevance of the information search",
          },
        ]);
        break;
      case "class_3":
        setObjectives([
          { key: "obj3_1", title: "To keep track of the student's activity" },
          { key: "obj3_2", title: "Observance of instructions" },
          { key: "obj3_3", title: "Safeguarding of productions" },
        ]);
        break;
      case "class_4":
        setObjectives([
          { key: "obj4_1", title: "Understanding of student behavior" },
          { key: "obj4_2", title: "To improve thinking" },
        ]);
    }
    setObjectiveValue(objectives[0]);
  };

  const handleObjectiveChange = (e) => {
    setObjectiveValue(e.target.key);
    switch (e.target.value) {
      case "Relationship to Time":
        setIndicators([
          {
            key: "ind_11_1",
            title: "Sites consulted by indicating the domain",
          },
          { key: "ind_11_2", title: "Time spent on a site " },
          { key: "ind_11_3", title: "Total time spent on the sites " },
          {
            key: "ind_11_4",
            title: "Percentage of site consultation in time ",
          },
          { key: "ind_11_5", title: "Used applications" },
          { key: "ind_11_6", title: "Time spent on an application" },
          { key: "ind_11_7", title: "Total time spent on applications" },
          {
            key: "ind_11_8",
            title:
              "Percentage of accessed applications in time, by application ",
          },
        ]);
        break;
      case "Maximize the efficiency of document search processing (consultation)":
        setIndicators([
          { key: "ind_12_1", title: "Number of sites consulted" },
          { key: "ind_12_2", title: "Number of accesses to a site" },
          { key: "ind_12_3", title: "Total number of site accesses" },
          { key: "ind_12_4", title: "Percentage of SC in number of accesses" },
          { key: "ind_12_5", title: "Number of accesses to a domain" },
          { key: "ind_12_6", title: "Total number of accesses to a domain" },
          { key: "ind_12_7", title: "Percentage of SC in domain" },
          { key: "ind_12_8", title: "Number of applications consulted" },
          { key: "ind_12_9", title: "Number of accesses to an application" },
          { key: "ind_12_10", title: "Total number of application accesses" },
          {
            key: "ind_12_11",
            title: "Percentage of CA by number of accesses ",
          },
          {
            key: "ind_12_12",
            title: "Level of efficiency (fast, min resources, correct result)",
          },
          {
            key: "ind_12_13",
            title: "Type of media consulted (with extension) ",
          },
          {
            key: "ind_12_14",
            title: "List of consulted supports (doc, audio, video, image ...)",
          },
          { key: "ind_12_15", title: "Number of media consulted " },
          { key: "ind_12_16", title: "Number of accesses to a medium " },
          {
            key: "ind_12_17",
            title: "Total number of accesses to consulted materials",
          },
          {
            key: "ind_12_18",
            title: "Percentage of CDs in number of accesses",
          },
          { key: "ind_12_19", title: "Time by type" },
          { key: "ind_12_20", title: "Status of a site" },
          { key: "ind_12_21", title: "Number of blacklisted sites" },
          { key: "ind_12_22", title: "Number of certified/official sites" },
          { key: "ind_12_23", title: "Number of official tools" },
          {
            key: "ind_12_24",
            title: "Number of relevant educational applications",
          },
          {
            key: "ind_12_25",
            title: "Number of irrelevant educational applications",
          },
        ]);
        break;
      case "Maximize the relevance of the information search":
        setIndicators([
          { key: "ind_21_1", title: "Duration of the research" },
          { key: "ind_21_2", title: "Time spent on each step" },
          {
            key: "ind_21_3",
            title: "Time remaining before the end indicated by the teacher",
          },
          {
            key: "ind_21_4",
            title: "Time added after the end indicated by the teacher",
          },
          { key: "ind_21_5", title: "Time spent to get a correct result" },
          { key: "ind_21_6", title: "The research carried out" },
          {
            key: "ind_21_7",
            title: "Number of attempts to succeed in your search",
          },
          { key: "ind_21_8", title: "Level of efficiency" },
          { key: "ind_21_9", title: "Relevance of resources" },
          { key: "ind_21_10", title: "Keyword relevance" },
          { key: "ind_21_11", title: "Relevance of results" },
          { key: "ind_21_12", title: "Status of the research carried out" },
          { key: "ind_21_13", title: "Research sequence" },
          {
            key: "ind_21_14",
            title: "Number of times a page is opened/closed",
          },
          { key: "ind_21_15", title: "Status of an activity" },
          { key: "ind_21_16", title: "List of information sources" },
          { key: "ind_21_17", title: "Key words" },
          { key: "ind_21_18", title: "Number of keywords" },
          { key: "ind_21_19", title: "Number of steps" },
          { key: "ind_21_20", title: "Error path to a source" },
          { key: "ind_21_21", title: "Number of route errors" },
          { key: "ind_21_22", title: "A source of information" },
          { key: "ind_21_23", title: "Cross-reference several sources" },
          { key: "ind_21_24", title: "Information capacity" },
        ]);
        break;
      case "To keep track of the student's activity":
        setIndicators([
          { key: "ind_31_1", title: "Indicator" },
          { key: "ind_31_2", title: "Indicator" },
          { key: "ind_31_3", title: "Indicator" },
          { key: "ind_31_4", title: "Indicator" },
          { key: "ind_31_5", title: "Indicator" },
          { key: "ind_31_6", title: "Indicator" },
          { key: "ind_31_7", title: "Indicator" },
          { key: "ind_31_8", title: "Indicator" },
        ]);
        break;
      case "Observance of instructions":
        setIndicators([
          { key: "ind_32_1", title: "Indicator" },
          { key: "ind_32_2", title: "Indicator" },
          { key: "ind_32_3", title: "Indicator" },
          { key: "ind_32_4", title: "Indicator" },
          { key: "ind_32_5", title: "Indicator" },
          { key: "ind_32_6", title: "Indicator" },
          { key: "ind_32_7", title: "Indicator" },
          { key: "ind_32_8", title: "Indicator" },
        ]);
        break;
      case "Safeguarding of productions":
        setIndicators([
          { key: "ind_33_1", title: "Indicator" },
          { key: "ind_33_2", title: "Indicator" },
          { key: "ind_33_3", title: "Indicator" },
          { key: "ind_33_4", title: "Indicator" },
          { key: "ind_33_5", title: "Indicator" },
          { key: "ind_33_6", title: "Indicator" },
          { key: "ind_33_7", title: "Indicator" },
          { key: "ind_33_8", title: "Indicator" },
        ]);
        break;
      case "Understanding of student behavior":
        setIndicators([
          { key: "ind_41_1", title: "Indicator" },
          { key: "ind_41_2", title: "Indicator" },
          { key: "ind_41_3", title: "Indicator" },
          { key: "ind_41_4", title: "Indicator" },
          { key: "ind_41_5", title: "Indicator" },
          { key: "ind_41_6", title: "Indicator" },
          { key: "ind_41_7", title: "Indicator" },
          { key: "ind_41_8", title: "Indicator" },
        ]);
        break;
      case "To improve thinking":
        setIndicators([
          { key: "ind_42_1", title: "Indicator" },
          { key: "ind_42_2", title: "Indicator" },
          { key: "ind_42_3", title: "Indicator" },
          { key: "ind_42_4", title: "Indicator" },
          { key: "ind_42_5", title: "Indicator" },
          { key: "ind_42_6", title: "Indicator" },
          { key: "ind_42_7", title: "Indicator" },
          { key: "ind_42_8", title: "Indicator" },
        ]);
    }
    setIndicatorValue(indicators[0]);
  };
  const handleIndicatorChange = (e) => {
    setIndicatorValue(e.target.key);
  };

  return (
    <div class="row">
      <div class="form-group row">
        <h3>
          Main indicator (<i>anchor</i>) for situation perception
        </h3>
        <panel>
          Decsribe what main indicators that allow you to perform your analysis
          and take the appropriate decision
        </panel>
        <hr className="my-1" />
      </div>
      <div className="col-md-6 mt-2">
        <form className="needs-validation" noValidate>
          <div className="input-group rounded">
            <input
              type="search"
              class="form-control rounded"
              placeholder="Search"
              aria-label="Search"
              aria-describedby="search-addon"
            />
            <span className="input-group-text border-0" id="search-addon">
              <i className="fas fa-search"></i>
            </span>
          </div>
          <div className="row">
            <label for="role" className="form-label">
              Class of the key indicator
            </label>
            <select
              className="form-select"
              id="role"
              placeholder="Your role"
              onChange={handleClasseChange}
              value={indicatorClass}
            >
              <option value="class_1">
                Monitoring the use of media and tools
              </option>
              <option value="class_2">Monitoring Information Retrieval</option>
              <option value="class_3">Monitoring of student activity</option>
              <option value="class_4">Monitoring comprehension</option>
            </select>
            <hr className="my-3" />

            <label for="objective" className="form-label">
              Objective of the key indicator
            </label>
            <select
              className="form-select"
              id="objective"
              onChange={handleObjectiveChange}
              value={objectiveValue}
            >
              {objectives.map((obj) => {
                return <option key={obj.key}>{obj.title}</option>;
              })}
            </select>
            <hr className="my-3" />

            <label for="name" className="form-label">
              Name of the key indicator
            </label>
            <select
              className="form-select"
              id="name"
              placeholder="Your role"
              value={indicatorValue}
              onChange={handleIndicatorChange}
            >
              {indicators.map((obj) => {
                return <option key={obj.key}>{obj.title}</option>;
              })}
            </select>
            <hr className="my-3" />
          </div>
        </form>
      </div>
      <div className="col-md-6 mt-2 px-4">
        <Chart />
      </div>
    </div>
  );
};
export default KPI;
