import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function AddFrame(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addFrame(data);
  };

  const addFrame = (data) => {
    axios.post("http://localhost:3001/frames", data).then(() => {
      props.setFrames([
        ...props.frames,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-frame" onSubmit={handleSubmit(onSubmit)}>
      <h4>Add Frame</h4>
      <input
        type="text"
        placeholder="Frame Title"
        name="frame_title"
        {...register('frame_title', { required: true })}

      />
      <input
        type="text"
        placeholder="Frame"
        name="frame_description"
        {...register('frame_description', { required: true })}

      />
      <input
        type="number"
        placeholder="Rating"
        name="frame_rating"
        {...register('frame_rating', { required: true })}

      />

      <input id="btn" type="submit" />
    </form>
  );
}
