import React, { useState } from "react";
import "./chart.css";

const Chart = (props) => {
    const allChartClasses=[
        {key:'deviation', title:'Deviation', description:"Emphasise variations (+/-) from a fixed reference point. Typically the reference point is zero but it can also be a target or a long-term average. Can also be used to show sentiment (positive/neutral/negative)"},
        {key:'correlation', title:'Correlation', description:"Show the relationship between two or more variables. Be mindful that, unless you tell them otherwise, many readers will assume the relationships you show them to be causal (i.e., one causes the other)."},
        {key:'ranking', title:'Ranking', description:"Use where an item’s position in an ordered list is more important than its absolute or relative value. Don’t be afraid to highlight the points of interest."},
        {key:'distribution', title:'Distribution', description:"Show values in a dataset and how often they occur. The shape (or ‘skew’) of a distribution can be a memorable way of highlighting the lack of uniformity or equality in the data."},
        {key:'evolution', title:'Evolution', description:"Give emphasis to changing trends. These can be short (intra-day) movements or extended series traversing decades or centuries: Choosing the correct time period is important to provide suitable context for the reader."},
        {key:'part_whole', title:'Part-to-Whole', description:"Show how a single entity can be broken down into its component elements. If the reader’s interest is solely in the size of the components, consider a magnitude-type chart instead."},
        {key:'magnitude', title:'Magnitude', description:"Show size comparisons. These can be relative (just being able to see larger/bigger) or absolute (need to see fine differences). Usually these show a ‘counted’ number (for example, barrels, dollars or people) rather than a calculated rate or per cent."},
        {key:'spatial', title:'Spatial', description:"Used only when precise locations or geographical patterns in data are more important to the reader than anything else."},
        {key:'flow', title:'Flow', description:"Show the reader volumes or intensity of movement between two or more states or conditions. These might be logical sequences or geographical locations."},
    ];
  const [chartTypes, setChartTypes] = useState([]);

  const [charts, setChart] = useState([]);

  const handleClassChange = (e) => {
    switch (e.target.value) {
      case "bar_class":
        setChartTypes([
          { key: "bar_h", title: "Vertical bar chart", description:"This type of graphics is used to..." },
          { key: "bar_v", title: "Horizontal bar chart", description:"This type of graphics is used to..." },
          { key: "bar_m", title: "Multi axis bar chart", description:"This type of graphics is used to..." },
        ]);
        break;
      default:
        setChartTypes([
          { key: "bar_h", title: "Vertical bar chart", description:"This type of graphics is used to..." },
          { key: "bar_v", title: "Horizontal bar chart", description:"This type of graphics is used to..." },
          { key: "bar_m", title: "Multi axis bar chart", description:"This type of graphics is used to..." },
        ]);
    }
    setChart(
      chartTypes.map((obj) => {
        return <option key={obj.key}>{obj.title}</option>;
      })
    );
  };

  const [visuElement, setVisuElement] = useState(
    <div className="card border-secondary mb-3">
      <div className="card-body text-secondary">
        <h5 className="card-title">Chard and graphics</h5>
        <p className="card-text">
          Select a chart to see a representation here
        </p>
      </div>
    </div>
  );

  const handleTypeChange = (e) => {
    let filtered = chartTypes.reduce(
      (a, o) => (o.title == e.target.value && a.push(o), a),
      []
    );
    
console.log(filtered[0].description);
    setVisuElement(
        <div className="card border-secondary mb-3">
      <div className="card-body text-secondary">
        <h5 className="card-title">{filtered[0].title}</h5>
        <img
          id="chart_stakeholder"
          className="card-img-top"
          src={process.env.PUBLIC_URL + "/charts/" + filtered[0].key + ".png"}
          alt="Card image cap"
        />
        
        <p className="card-text">
        <hr className="my-3" />
        {filtered[0].description}
        </p>
      </div>
    </div>
       
      
    );
  };

  return (
    <div className="row">
      <div className="row">
        <label for="chart" className="form-label">
          Class of charts
        </label>
        <select
          className="form-select"
          id="chart"
          placeholder="chartclass"
          onChange={handleClassChange}
          required
        >
          <option value="bar_class">Bar charts</option>
          <option value="line_class">Line charts</option>
          <option value="area_class">Area charts</option>
          <option value="other_class">Other charts</option>
          <option value="deviation_class">Deviation</option>
          <option value="Correlation_class">Correlation</option>
          <option value="other_class"></option>
          <option value="other_class"></option>
          <option value="other_class"></option>
          <option value="other_class"></option>
          <option value="other_class"></option>
          <option value="other_class"></option>
          <option value="other_class"></option>          
        </select>
      </div>
      <div className="row"></div>
      <label for="chart" className="form-label">
        Chart name
      </label>
      <select
        className="form-select"
        id="chartname"
        placeholder="chartname"
        onChange={handleTypeChange}
        required
      >
        {charts}
      </select>
      <hr className="my-3" />
      {visuElement}
    </div>
  );
};
export default Chart;
