import "./library-view.css";
import React, { useState, useEffect } from "react";
import Actions from "./frames_lib/frame_actions";
import axios from "axios";
import Collapsible from "react-collapsible";
import {
  AddBoxRounded,
  EditRounded,
  HighlightOff,
  HighlightOffRounded,
  Loupe,
  RemoveCircle,
} from "@material-ui/icons";
import FrameItem from "./frames_lib/frame_items";
import { EyeFill } from "react-bootstrap-icons";

export const FrameContext = React.createContext();

function Frames() {
  const [frames, setFrames] = useState([]);
  const [panels, setPanels] = useState([]);

  const [showModal, setShowModal] = useState(false);
  const [showItemModal, setShowItemModal] = useState(false);

  const [currentFrame, setCurrentFrame] = useState();
  const [currentPanel, setCurrentPanel] = useState();
  const [pList, setpList] = useState();
  const [visualizations, setVisualizations] = useState();
  const [indicators, setIndicators] = useState();

  const frameContext = [
    currentFrame,
    setCurrentFrame,
    currentPanel,
    setCurrentPanel,
    pList,
    setpList,
    panels
  ];

  const [creationMode, setCreationMode] = useState(true);

  const handleModalClose = () => {
    setShowModal(false);
  };
  const handleItemModalClose = () => {
    setShowItemModal(false);
  };

  const handleItemModalSave = () => {
    let element = currentFrame;
    let plist = {
      frame_id: currentFrame.id,
      panel_id: currentPanel.id,
    };
    axios.post("http://localhost:3001/plist", plist).then(() => {
      updateDisplay();
    });

    setShowItemModal(false);
    updateDisplay();
  };

  const handleModalSave = () => {
    let element = currentFrame;

    //return;
    if (creationMode)
      axios.post("http://localhost:3001/frames", element).then(() => {
        setFrames([
          ...frames,
          {
            data: element,
            headers: { "Content-Type": "multipart/form-data" },
          },
        ]);

        updateDisplay();
      });
    else
      axios.put("http://localhost:3001/frames", element).then((res) => {
        setFrames(
          frames.map((item) => {
            return item.id === element.id
              ? {
                  id: item.id,
                  title: item.title,
                  description: item.description,
                  class:''
                }
              : item;
          })
        );
        updateDisplay();
      });

    setShowModal(false);
    updateDisplay();
  };

  const deleteFrame = (id) => {
    window.confirm("Are you sure you want to delete this type of frame?")
      ? axios.delete(`http://localhost:3001/frames/${id}`).then((res) => {
          setFrames(
            frames.filter((item) => {
              return item.id !== id;
            })
          );
        })
      : (id = id);
    updateDisplay();
  };

  const getFrames = () => {
    axios.get("http://localhost:3001/frames").then((res) => {
      setFrames(res.data);
    });
  };
  const getFrameItems = (item) => {
    if (!pList) return [];
    if (!item) return [];
    let elts = pList.filter((i) => i.frame_id === item.id);
    return elts;
  };

  const getPanByID = (id) => {
    let elt = {
      title: " ",
      description: " ",
    };
    if (panels) elt = panels.find((x) => x.id === id);

    return elt;
  };

  const decodeChart = (elt) => {
    if (!elt) return; 
    let blob = elt.chart;
    if (!blob) return;
    if (typeof blob === "string") return blob;
    const { data } = blob;
    const img = new Buffer.from(data).toString("ascii");
    
    return img;
  };

 

  const displayCPanItem = (id) => {
    alert("TODO");
  };
  const dropCPanItem = (id) => {
    let i = 0;
    window.confirm("Are you sure you want to delete this panel?")
      ? axios.delete(`http://localhost:3001/plist/${id}`).then((res) => {
          setpList(
            pList.filter((item) => {
              return item.id !== id;
            })
          );
        })
      : (i = 1);
  };

  const getPanels = () => {
    axios.get("http://localhost:3001/panels").then((res) => {
      setPanels(res.data);
    });
  };

  const getpList = () => {
    axios.get("http://localhost:3001/plist").then((res) => {
      setpList(res.data);
    });
  };

  const getIndicators = () => {
    axios.get("http://localhost:3001/indicators").then((res) => {
      setIndicators(res.data);
    });
  };
  const getVisualizations = () => {
    axios.get("http://localhost:3001/visualizations").then((res) => {
      setVisualizations(res.data);
    });
  };

  useEffect(() => {
    getFrames();
    getPanels();
    getpList();
    getIndicators();
    getVisualizations();
  }, []);

  const updateDisplay = () => {
    getFrames();
    getPanels();
    getpList();
    getIndicators();
    getVisualizations();
  };

  const fetchPanelElement = (id) => {
    if (typeof id === "undefined") return { title: null, chart: null };
    else {
      let elt = panels.find((x) => x.id === id);
      if (elt) return elt;
      else return { title: null, description: null };
    }
  };

  const AddFrameItems = (item) => {
    let elt = pList.find((x) => x.frame_id === item.id);

    setCurrentFrame(item);
    setCurrentPanel(panels[0]);
    setShowItemModal(true);
  };
  const DeleteFrameItems = (item) => {
    let elt = pList.find((x) => x.frame_id === item.id);
    console.log(pList);
  };

  const CreateNewFrame = () => {
    const item = {
      title: "NewFrame",
      description: "Description",
    };
    setCurrentFrame(item);
    setCreationMode(true);
    setShowModal(true);

    return;
  };

  const EditFrame = (item) => {
    console.log(item);
    setCurrentFrame(item);
    setCreationMode(false);
    setShowModal(true);
    return;
  
  };
  return (
    <FrameContext.Provider value={frameContext}>
      <Actions
        title="un titre"
        show={showModal}
        mode="Creation"
        frames={frames}
        setFrames={setFrames}
        updateDisplay={updateDisplay}
        onClose={handleModalClose}
        onSave={handleModalSave}
      />
       <FrameItem
        title="un titre"
        show={showItemModal}
        mode="Creation"
        frames={frames}
        setFrames={setFrames}
        updateDisplay={updateDisplay}
        onClose={handleItemModalClose}
        onSave={handleItemModalSave}
      />
 
      <div>
        <button className="btn btn-success" onClick={CreateNewFrame}>
          <AddBoxRounded /> &nbsp; New Frame Type
        </button>
      </div>
      <table className="table table-bordered table-hover table-dark table-striped text-md-start">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Title</th>
            <th scope="col">Class</th>
            <th scope="col">Description</th>
            <th scope="col">Panels</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {frames.map((item) => {
            return (
              <tr key={item.id}>
                <th scope="row">{item.id}</th>
                <td><h6>{item.title}</h6></td>
                <td><i>{item.class}</i></td>
                <td>{item.description}</td>
                <td>
                  <span
                    className="btn-sm btn-outline-info"
                    role="button"
                    onClick={() => AddFrameItems(item)}
                  >
                    <Loupe /> Simple Panel
                  </span>
                  &nbsp;&nbsp;&nbsp;

                  <span
                    className="btn-sm btn-outline-info"
                    role="button"
                    onClick={() => AddFrameItems(item)}
                  >
                    <Loupe /> Composite Panel
                  </span>
                  <br />
                  <br />
                  <ol className="list-group-flush">
                    {getFrameItems(item).map((i) => {
                      return (
                        <li className="list-group-item d-flex justify-content-between align-items-center">
                          <h6>{getPanByID(i.panel_id).title}</h6>
                          
                          <span
                            role="button"
                            className="text-danger btn-sm"
                            onClick={() => dropCPanItem(i.id)}
                          >
                            <RemoveCircle />
                          </span>{" "}
                        </li>
                      );
                    })}
                  </ol>
                  <i>{fetchPanelElement(item.id).title}</i>
                  {/*  <div className="chart_box">                  
                    <img alt="Chart" src={decodeChart(fetchPanelElement(item.visualization_id).chart)} /> </div>*/}
                </td>

                <td>
                  <button
                    className="btn btn-sm btn-outline-primary"
                    onClick={() => EditFrame(item)}
                  >
                    <EditRounded />
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => deleteFrame(item.id)}
                  >
                    <HighlightOffRounded />
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </FrameContext.Provider>
  );
}

export default Frames;
