import "./library-view.css";

import { useState, useEffect } from "react";
import AddFrame from "./frames_lib/add_frame";
import Edit from "./frames_lib/edit_frame";
import Delete from "./frames_lib/delete_frame";
import axios from "axios";

function Frames() {
  const [frames, setFrames] = useState([]);

  const getFrames = () => {
    axios.get("http://localhost:3001/frames").then((res) => {
      setFrames(res.data);
    });
  };

  useEffect(() => {
    getFrames();
  }, [frames]);

  return (
    <>
      <AddFrame frames={frames} setFrames={setFrames} />
      <div className="frames">
        {frames.map((item) => {
          return (
            <div className="frame">
              <h3>Title: {item.frame_title}</h3>
              <h3>Review: {item.frame_description}</h3>
              <h3>Rating: {item.frame_rating}</h3>
              <Edit id={item.id} frames={frames} setFrames={setFrames} />
              <Delete id={item.id} frames={frames} setFrames={setFrames} />
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Frames;
