import "./library-view.css";

import { useState, useEffect } from "react";
import AddVisualization from "./visualizations_lib/add_visualization";
import Edit from "./visualizations_lib/edit_visualization";
import Delete from "./visualizations_lib/delete_visualization";
import axios from "axios";

function Visualizations() {
  const [visualizations, setVisualizations] = useState([]);

  const getVisualizations = () => {
    axios.get("http://localhost:3001/visualizations").then((res) => {
      setVisualizations(res.data);
    });
  };

  useEffect(() => {
    getVisualizations();
  }, [visualizations]);

  return (
    <>
      <AddVisualization visualizations={visualizations} setVisualizations={setVisualizations} />
      <div className="visualizations">
        {visualizations.map((item) => {
          return (
            <div className="visualization">
              <h3>Title: {item.visualization_title}</h3>
              <h3>Review: {item.visualization_description}</h3>
              <h3>Rating: {item.visualization_rating}</h3>
              <Edit id={item.id} visualizations={visualizations} setVisualizations={setVisualizations} />
              <Delete id={item.id} visualizations={visualizations} setVisualizations={setVisualizations} />
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Visualizations;
