import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function Edit(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    data["id"] = props.id;
    updateFrame(data);
  };

  const updateFrame = (data) => {
    axios.put("http://localhost:3001/frames", data).then((res) => {
      props.setFrames(
        props.frames.map((item) => {
          return item.id === props.id
            ? {
                id: item.id,
                frame_title: item.frame_title,
                frame_description: item.frame_description,
                frame_rating: item.frame_rating,
              }
            : item;
        })
      );
    });
  };

  return (
    <form className="add-frame" onSubmit={handleSubmit(onSubmit)}>
      <h4>Edit Frame</h4>
      <input
        type="text"
        placeholder="Frame"
        name="frame_description"
        {...register('frame_description', { required: true })}
      />
      <input
        type="number"
        placeholder="Rating"
        name="frame_rating"
        {...register('frame_rating', { required: true })}
      />

      <input id="btn" type="submit" />
    </form>
  );
}
