import React, { useState } from "react";
import ReactJson from "react-json-view";
import jsonfile from "jsonfile";
import dash from "../../../data/template_dash.json";
import FileSaver from "file-saver";

import "./generate-component.css";
const GenerateComponent = () => {
  const file = "../../../data/template_dash.json";
  const readJSonFile = () => {
    const filename = "f";
    const fileData = JSON.stringify(dash);
    const blob = new Blob([fileData], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = `${filename}.json`;
    link.href = url;
    //link.click();

    var file = new File(["Hello, world!"], " ");
    FileSaver.saveAs(file);
  };
  return (
    <div className="row">
      <div className="card  row">
      <div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>
        <span className="card-header bg-secondary">
          Dashboard JSon structure
          <button className="btn-success btn-sm" onClick={readJSonFile}>
            Save the dashboard
          </button>
        </span>
        <div className="card-body">
        <h5 className='row'>Dashboard JSon structure
          <button className="btn-success btn-sm" onClick={readJSonFile}>
            Save the dashboard
          </button></h5>
          <ReactJson src={dash} collapsible view="dual" />
        </div>
      </div>
    </div>
  );
};

export default GenerateComponent;
