import React, { useState } from "react";
import ImageUploader from "react-images-upload";

const ChartUpload = (props) => {
  console.log(props);
    const [picture, setPicture] = useState(null);
    const [imgData, setImgData] = useState(null);
    const onChangePicture = e => {
      if (e.target.files[0]) {
        console.log("picture: ", e.target.files);
        props.onChartChange('reader.result');
        //props.register("chart", { required: true })
        
        setPicture(e.target.files[0]);
        const reader = new FileReader();
        reader.addEventListener("load", () => {
          setImgData(reader.result);
          
        });
        reader.readAsDataURL(e.target.files[0]);
      }
    };
    return (
              <div className="formInstructionsDiv formElement">
                <p className="instructionsText" />
                <div className="register_profile_image">
                  <input id="chart" type="file"   onChange={onChangePicture}       
                  accept="image/*"/>
                </div>
                
                <div className="chart_box">
                  <img className="chart_box-image" src={imgData} />
                </div>
              </div>
    );
};

export default ChartUpload;
