import "../library-view.css";
import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";

export default function AddDecision(props) {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data);
    addDecision(data);
  };

  const addDecision = (data) => {
    axios.post("http://localhost:3001/decisions", data).then(() => {
      props.setDecisions([
        ...props.decisions,
        {
          data,
        },
      ]);
    });
  };

  return (
    <form className="add-decision" onSubmit={handleSubmit(onSubmit)}>
      <h4>Describe a new type of decisions</h4>
      Title
      <input
        type="text"
        placeholder="Decision Title"
        name="decision_title"
        {...register("decision_title", { required: true })}
      />
      Description
      <textarea
        type="text"
        placeholder="Description..."
        name="decision_description"
        {...register("decision_description", { required: true })}
      />
     Class
      <select
        class="formselect"
        placeholder="Rating"
        name="decision_rating"
        {...register("decision_rating", { required: true })}
      >
        <option value="1">Strategic</option>
        <option value="2">Tactical</option>
        <option value="3">Operational</option>
      </select>

      <input className="btn btn-primary" type="submit" />
    </form>
  );
}
