import "../library-view.css";
import React, { useState, useContext } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import ChartUpload from "../chart-upload";
import Modal from "react-bootstrap/Modal";
import { Button } from "react-bootstrap";
import { VizContext } from "../visualizations";

const Actions = (props) => {
  const [element, setElement] = useContext(VizContext);

  const assignElement = (e) => {
    console.log(e);
    setElement(e);
    console.log(element);
  };

  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    data["id"] = element.id;
    data.chart = chart;

    updateVisualization(data);
  };

  const updateVisualization = (data) => {
    axios.put("http://localhost:3001/visualizations", data).then((res) => {
      props.setVisualizations(
        props.visualizations.map((item) => {
          return item.id === props.id
            ? {
                id: item.id,
                title: item.title,
                description: item.description,
                class: item.class,
                chart: item.chart,
              }
            : item;
        })
      );
    });
    props.updateDisplay();
  };

  const [chart, setChart] = React.useState(null);
  const [show, setShow] = React.useState(false);

  const handleChartChange = (v) => {
    setChart(v);
  };

  const handleElementUpdate=(e)=>{
    console.log(e);
   // setElement(e);
    console.log(element);
  }
  return (
    <>
    
      {element === undefined ? (
        "undefined"
      ) : (
        <div
          show={props.show}
          backdrop="static"
          keyboard={true}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          dialogClassName="modal-style"
          scrollable
          centered
        >
          <Modal.Header>
            <Modal.Title>{element.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form
              className="add-visualization"
              onSubmit={handleSubmit(onSubmit)}
            >
              <table className="table table-dark">
                <tbody>
                  <tr>
                    <td>Name</td>
                    <td>
                      <input
                        type="text"
                        placeholder="Visualization Title"
                        onChange={ (event) => console.log("onchange is triggered") }
                        name="title"
                        {...register("title", { required: true })}
                      />
                    </td>
                    <td rowSpan="4">
                      <b>Chart</b>
                      <ChartUpload onChartChange={handleChartChange} />
                    </td>
                  </tr>
                  <tr>
                    <td>Description</td>
                    <td>
                      <textarea
                        type="text"
                        placeholder="Description..."
                        value={element.description}
                        onChange={handleElementUpdate}
                        name="description"
                        {...register("description", { required: true })}
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>Class</td>
                    <td>
                      <select
                        className="formselect"
                        value={element.class}
                        name="class"
                        {...register("class", { required: true })}
                      >
                        <option value="Distribution">Distribution</option>
                        <option value="Correlation">Correlation</option>
                        <option value="Ranking">Ranking</option>
                        <option value="Part of a Whole">Part of a Whole</option>
                        <option value="Evolution">Evolution</option>
                        <option value="Map">Map</option>
                        <option value="Flow">Flow</option>
                      </select>
                    </td>
                  </tr>
                  <tr>
                    <td></td>
                    <td>
                      <input
                        className="btn btn-primary"
                        value="Submit"
                        type="submit"
                      />
                    </td>
                  </tr>
                </tbody>
              </table>


            
            </form>
          </Modal.Body>
          <Modal.Footer>
            <Button type="button" size="sm" variant="secondary" onClick={props.onClose}>
              Cancel
            </Button>
            <Button type="submit" size="sm" variant="secondary">
              Save
            </Button>
          </Modal.Footer>
        </div>
      )}
    </>
  );
};
export default Actions;
