import "./library-view.css";

import { useState, useEffect } from "react";
import AddDecision from "./decisions_lib/add_decision";
import Edit from "./decisions_lib/edit_decision";
import Delete from "./decisions_lib/delete_decision";
import axios from "axios";

function Decisions() {
  const [decisions, setDecisions] = useState([]);

  const getDecisions = () => {
    axios.get("http://localhost:3001/decisions").then((res) => {
      setDecisions(res.data);
    });
  };

  useEffect(() => {
    getDecisions();
  }, [decisions]);

  return (
    <>
      <table className="table table-hover table-dark">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Title</th>
            <th scope="col">Type</th>
            <th scope="col">Description</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {decisions.map((item) => {
            return (
              <tr>
                <th scope="row">1</th>
                <td>{item.decision_title}</td>
                <td>{item.decision_rating}</td>
                <td>{item.decision_description}</td>
                <td><Edit
                id={item.id}
                decisions={decisions}
                setDecisions={setDecisions}
              />
              <Delete
                id={item.id}
                decisions={decisions}
                setDecisions={setDecisions}
              /></td>
              </tr>
            );
          })}

         
        </tbody>
      </table>
      <AddDecision decisions={decisions} setDecisions={setDecisions} />
      <div className="decisions">
        {decisions.map((item) => {
          return (
            <div className="decision">
              <h3>Title: {item.decision_title}</h3>
              <h3>Review: {item.decision_description}</h3>
              <h3>Rating: {item.decision_rating}</h3>
              <Edit
                id={item.id}
                decisions={decisions}
                setDecisions={setDecisions}
              />
              <Delete
                id={item.id}
                decisions={decisions}
                setDecisions={setDecisions}
              />
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Decisions;
