import React, { useState } from "react";
import ReactJson from "react-json-view";
import jsonfile from "jsonfile";
import dash from "../../../data/template_dash.json";
import FileSaver from "file-saver";

import "./generate-component.css";
const GenerateComponent = () => {
  
  
  const filename = "generated_dashboard";
  const fileData = JSON.stringify(dash);
  const blob = new Blob([fileData], { type: "text/plain" });
  const [file, setFile]=useState({
    "squadName": "Super hero squad",
    "homeTown": "Metro City",
    "formed": 2016,
    "secretBase": "Super tower",
    "active": true,
    "members": [
      {
        "name": "Molecule Man",
        "age": 29,
        "secretIdentity": "Dan Jukes",
        "powers": [
          "Radiation resistance",
          "Turning tiny",
          "Radiation blast"
        ]
      },
      {
        "name": "Madame Uppercut",
        "age": 39,
        "secretIdentity": "Jane Wilson",
        "powers": [
          "Million tonne punch",
          "Damage resistance",
          "Superhuman reflexes"
        ]
      },
      {
        "name": "Eternal Flame",
        "age": 1000000,
        "secretIdentity": "Unknown",
        "powers": [
          "Immortality",
          "Heat Immunity",
          "Inferno",
          "Teleportation",
          "Interdimensional travel"
        ]
      }
    ]
  });
  const readJSonFile = () => {
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = `${filename}.json`;
    link.href = url;
    link.click();

  };
  const saveJSonFile = () => {
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = `${filename}.json`;
    link.href = url;
    link.click();

    var file = new File(["Hello, world!"], " ");
    //FileSaver.saveAs(file);
  };
  return (
    <div className="  row">
      <div class="bg-secondary card-header d-flex flex-row">
        <div class="p-2 col-sm-6">Dashboard JSon structure</div>
        <div class="p-2 text-right col-sm-3">
          <span className="btn btn-primary btn-sm " onClick={readJSonFile}>
            Generat the structure
          </span>
        </div>
        <div class="p-2 text-right col-sm-3">
          <span className="btn btn-success btn-sm " onClick={saveJSonFile}>
            Save the dashboard
          </span>
        </div>
      </div>
      <div className="card-body bg-light">
        <ReactJson src={file} collapsible view="dual" />
      </div>
    </div>
  );
};

export default GenerateComponent;
