import React, { useState } from "react";
import IndicatorChooser from "./indicator-chooser";
import Chart from "./chart";
import Select from "react-select";

const KPI = (props) => {
  const [objectives, setObjectives] = useState([]);
  const [indicators, setIndicators] = useState([]);
  const [indicatorClass, setIndicatorClass] = useState();
  const [objectiveValue, setObjectiveValue] = useState();
  const [indicatorValue, setIndicatorValue] = useState();

  const handleClasseChange = (e) => {
    
    switch (e.value) {
      case "class_1":
        setObjectives([
          { value: "obj1_1", label: "Relationship to Time" },
          {
            value: "obj1_2",
            label:
              "Maximize the efficiency of document search processing (consultation)",
          },
        ]);
        break;
      case "class_2":
        setObjectives([
          {
            value: "obj2_1",
            label: "Maximize the relevance of the information search",
          },
        ]);
        break;
      case "class_3":
        setObjectives([
          { value: "obj3_1", label: "To keep track of the student's activity" },
          { value: "obj3_2", label: "Observance of instructions" },
          { value: "obj3_3", label: "Safeguarding of productions" },
        ]);
        break;
      case "class_4":
        setObjectives([
          { value: "obj4_1", label: "Understanding of student behavior" },
          { value: "obj4_2", label: "To improve thinking" },
        ]);
    }
    setObjectiveValue(objectives[0]);
  };

  const handleObjectiveChange = (e) => {
    setObjectiveValue(e.value);
    switch (e.value) { 
      case "Relationship to Time":
        setIndicators([
          {
            key: "ind_11_1",
            label: "Sites consulted by indicating the domain",
          },
          { key: "ind_11_2", label: "Time spent on a site " },
          { key: "ind_11_3", label: "Total time spent on the sites " },
          {
            key: "ind_11_4",
            label: "Percentage of site consultation in time ",
          },
          { key: "ind_11_5", label: "Used applications" },
          { key: "ind_11_6", label: "Time spent on an application" },
          { key: "ind_11_7", label: "Total time spent on applications" },
          {
            key: "ind_11_8",
            label:
              "Percentage of accessed applications in time, by application ",
          },
        ]);
        break;
      case "Maximize the efficiency of document search processing (consultation)":
        setIndicators([
          { key: "ind_12_1", label: "Number of sites consulted" },
          { key: "ind_12_2", label: "Number of accesses to a site" },
          { key: "ind_12_3", label: "Total number of site accesses" },
          { key: "ind_12_4", label: "Percentage of SC in number of accesses" },
          { key: "ind_12_5", label: "Number of accesses to a domain" },
          { key: "ind_12_6", label: "Total number of accesses to a domain" },
          { key: "ind_12_7", label: "Percentage of SC in domain" },
          { key: "ind_12_8", label: "Number of applications consulted" },
          { key: "ind_12_9", label: "Number of accesses to an application" },
          { key: "ind_12_10", label: "Total number of application accesses" },
          {
            key: "ind_12_11",
            label: "Percentage of CA by number of accesses ",
          },
          {
            key: "ind_12_12",
            label: "Level of efficiency (fast, min resources, correct result)",
          },
          {
            key: "ind_12_13",
            label: "Type of media consulted (with extension) ",
          },
          {
            key: "ind_12_14",
            label: "List of consulted supports (doc, audio, video, image ...)",
          },
          { key: "ind_12_15", label: "Number of media consulted " },
          { key: "ind_12_16", label: "Number of accesses to a medium " },
          {
            key: "ind_12_17",
            label: "Total number of accesses to consulted materials",
          },
          {
            key: "ind_12_18",
            label: "Percentage of CDs in number of accesses",
          },
          { key: "ind_12_19", label: "Time by type" },
          { key: "ind_12_20", label: "Status of a site" },
          { key: "ind_12_21", label: "Number of blacklisted sites" },
          { key: "ind_12_22", label: "Number of certified/official sites" },
          { key: "ind_12_23", label: "Number of official tools" },
          {
            key: "ind_12_24",
            label: "Number of relevant educational applications",
          },
          {
            key: "ind_12_25",
            label: "Number of irrelevant educational applications",
          },
        ]);
        break;
      case "Maximize the relevance of the information search":
        setIndicators([
          { key: "ind_21_1", label: "Duration of the research" },
          { key: "ind_21_2", label: "Time spent on each step" },
          {
            key: "ind_21_3",
            label: "Time remaining before the end indicated by the teacher",
          },
          {
            key: "ind_21_4",
            label: "Time added after the end indicated by the teacher",
          },
          { key: "ind_21_5", label: "Time spent to get a correct result" },
          { key: "ind_21_6", label: "The research carried out" },
          {
            key: "ind_21_7",
            label: "Number of attempts to succeed in your search",
          },
          { key: "ind_21_8", label: "Level of efficiency" },
          { key: "ind_21_9", label: "Relevance of resources" },
          { key: "ind_21_10", label: "Keyword relevance" },
          { key: "ind_21_11", label: "Relevance of results" },
          { key: "ind_21_12", label: "Status of the research carried out" },
          { key: "ind_21_13", label: "Research sequence" },
          {
            key: "ind_21_14",
            label: "Number of times a page is opened/closed",
          },
          { key: "ind_21_15", label: "Status of an activity" },
          { key: "ind_21_16", label: "List of information sources" },
          { key: "ind_21_17", label: "Key words" },
          { key: "ind_21_18", label: "Number of keywords" },
