import React, { useState } from "react";
import Chart from "../steps/chart";
import Select from "react-select";
import "./frame.css";
import FrameIndicator from "./frame-indicator";

const Frame = (props) => {
  const [frames, setFrames] = useState([<FrameIndicator />])
  const frameAdd=()=>{
    let f = frames;
    f.concat(<FrameIndicator />)
    setFrames(frames.concat(<FrameIndicator />));
    console.log(frames);
  }
  
  return (
    <div class="row ">
    <div className='col-md-4 f_buttons'>
    
    <span
      className="btn btn-danger"
      onClick={() => frameAdd()}
    >
      Add a frame
    </span>
    </div>     
      {frames}
      <hr/>
      
      
    </div>
  );
};
export default Frame;
