import React, { useState } from "react";
import IndicatorChooser from "./indicator-chooser";

const KPI = (props) => {
  let objectives = [];
  let indicators = ['indicator'];

  const [objectiveOptions, setObjectiveOptions] = useState(
    objectives.map((obj) => {
      return <option key={obj}>{obj}</option>;
    })
  );

  const [indicatorOptions, setIndicatorOptions] =useState(
   indicators.map((obj) => {
    return <option key={obj}>{obj}</option>;
  })
  );

  const handleClasseChange = (e) => {
    switch (e.target.value) {
      case "class_1":
        objectives = [
          { key: "obj1_1",  title: "Relationship to Time" },
          {
            key: "obj1_2",
            title:
              "Maximize the efficiency of document search processing (consultation)",
          },
        ];
        break;
      case "class_2":
        objectives = [
          {
            key: "obj2_1",
            title: "Maximize the relevance of the information search",
          },
        ];
        break;
      case "class_3":
        objectives = [
          { key: "obj3_1", title: "To keep track of the student's activity" },
          { key: "obj3_2", title: "Observance of instructions" },
          { key: "obj3_3", title: "Safeguarding of productions" },
        ];
        break;
      case "class_4":
        objectives = [
          { key: "obj4_1", title: "Understanding of student behavior" },
          { key: "obj4_2", title: "To improve thinking" },
        ];
    }
    setObjectiveOptions(
      objectives.map((obj) => {
        return <option key={obj.key}>{obj.title}</option>;
      })
    );
  };

  const handleObjectiveChange = (e) => {
    console.log(e.target.key);
    switch (e.target.value) {
      case "Relationship to Time":
        indicators = [
        { key: "ind_11_1", title: "Sites consulted by indicating the domain" },
        { key: "ind_11_2", title: "Time spent on a site " },
        { key: "ind_11_3", title: "Total time spent on the sites " },
        { key: "ind_11_4", title: "Percentage of site consultation in time " },
        { key: "ind_11_5", title: "Used applications" },
        { key: "ind_11_6", title: "Time spent on an application" },
        { key: "ind_11_7", title: "Total time spent on applications" },
        { key: "ind_11_8", title: "Percentage of accessed applications in time, by application " },
      ];
        break;
      case "Maximize the efficiency of document search processing (consultation)":
        indicators = [
          { key: "ind_12_1", title: "Number of sites consulted" },
          { key: "ind_12_2", title: "Number of accesses to a site" },
          { key: "ind_12_3", title: "Total number of site accesses" },
          { key: "ind_12_4", title: "Percentage of SC in number of accesses" },
          { key: "ind_12_5", title: "Number of accesses to a domain" },
          { key: "ind_12_6", title: "Total number of accesses to a domain" },
          { key: "ind_12_7", title: "Percentage of SC in domain" },
          { key: "ind_12_8", title: "Number of applications consulted" },
          { key: "ind_12_9", title: "Number of accesses to an application" },
          { key: "ind_12_10", title: "Total number of application accesses" },
          { key: "ind_12_11", title: "Percentage of CA by number of accesses " },
          { key: "ind_12_12", title: "Level of efficiency (fast, min resources, correct result)" },
          { key: "ind_12_13", title: "Type of media consulted (with extension) " },
          { key: "ind_12_14", title: "List of consulted supports (doc, audio, video, image ...)" },
          { key: "ind_12_15", title: "Number of media consulted " },
          { key: "ind_12_16", title: "Number of accesses to a medium " },
          { key: "ind_12_17", title: "Total number of accesses to consulted materials" },
          { key: "ind_12_18", title: "Percentage of CDs in number of accesses" },
          { key: "ind_12_19", title: "Time by type" },
          { key: "ind_12_20", title: "Status of a site" },
          { key: "ind_12_21", title: "Number of blacklisted sites" },
          { key: "ind_12_22", title: "Number of certified/official sites" },
          { key: "ind_12_23", title: "Number of official tools" },
          { key: "ind_12_24", title: "Number of relevant educational applications" },
          { key: "ind_12_25", title: "Number of irrelevant educational applications" },
        ];
        break;
      case "Maximize the relevance of the information search":
        indicators = [
          { key: "ind_21_1", title: "Duration of the research" },
          { key: "ind_21_2", title: "Time spent on each step" },
          { key: "ind_21_3", title: "Time remaining before the end indicated by the teacher" },
          { key: "ind_21_4", title: "Time added after the end indicated by the teacher" },
          { key: "ind_21_5", title: "Time spent to get a correct result" },
