          { key: "ind_31_8", title: "Indicator" },
        ];
        break;
      case "Observance of instructions":
        indicators = 
        [
          { key: "ind_32_1", title: "Indicator" },
          { key: "ind_32_2", title: "Indicator" },
          { key: "ind_32_3", title: "Indicator" },
          { key: "ind_32_4", title: "Indicator" },
