import "./library-view.css";

import { useState, useEffect } from "react";
import AddFrame from "./frames_lib/add_frame";
import Edit from "./frames_lib/edit_frame";
import Delete from "./frames_lib/delete_frame";
import axios from "axios";

function Frames() {
  const [frames, setFrames] = useState([]);

  const getFrames = () => {
    axios.get("http://localhost:3001/frames").then((res) => {
      setFrames(res.data);
    });
  };

  useEffect(() => {
    getFrames();
  }, [frames]);

  return (
    <>
    <h4>Introduce a new type of frames</h4>
    <AddFrame frames={frames} setFrames={setFrames} />

    <h4>View, edit or delete exsting types of frames</h4>
      <table className="table table-bordered table-hover table-dark table-striped text-md-start">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Title</th>
            <th scope="col">Type</th>
            <th scope="col">Description</th>
            <th scope="col">Edit</th>
            <th scope="col">Delete</th>
          </tr>
        </thead>
        <tbody>
          {frames.map((item) => {
            return (
              <tr>
                <th scope="row">{item.id}</th>
                <td>{item.frame_title}</td>
                <td>{item.frame_rating ==1 ? "Strategic":(item.frame_rating ==2 ?"Tactical":"Operational")}</td>
                <td>{item.frame_description}</td>
                <td><Edit
                id={item.id}
                frames={frames}
                setFrames={setFrames}
              /></td>
              <td>
              <Delete
                id={item.id}
                frames={frames}
                setFrames={setFrames}
              /></td>
              </tr>
            );
          })}

         
        </tbody>
      </table>
      
      
    </>
  );
}

export default Frames;
